mapper.select:
SELECT p.id, p.title, p.content, p.summary, p.cover_image, p.thumbnail, p.category_id, p.author_id, p.status, p.view_count, p.like_count, p.created_at, p.updated_at, p.created_by, p.updated_by, p.deleted_at, c.id as cat_id, c.name as cat_name, c.description as cat_description, u.id as author_user_id, u.username as author_username, u.email as author_email, u.avatar_url as author_avatar_url, (SELECT COUNT(*) FROM comments cm WHERE cm.post_id = p.id AND cm.deleted_at IS NULL) as comment_count FROM posts p LEFT JOIN categories c ON p.category_id = c.id LEFT JOIN users u ON p.author_id = u.id WHERE p.deleted_at IS NULL
AND p.status = #{status}
AND p.status = 'published'
AND p.author_id = #{authorId}
AND p.category_id = #{categoryId}
AND EXISTS (SELECT 1 FROM post_tags pt2 WHERE pt2.post_id = p.id AND pt2.tag_id = #{tagId})
AND (p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%') OR p.summary LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY p.created_at DESC

mapper.update:
UPDATE posts SET
    deleted_at = #{deletedAt},
    updated_by = #{updatedBy}
WHERE id = #{id}
AND deleted_at IS NULL

UPDATE posts SET
    status = #{status},
    updated_at = #{updatedAt},
    updated_by = #{updatedBy}
WHERE id = #{id}
AND deleted_at IS NULL

mapper.select:
SELECT COUNT(*)
FROM posts p
WHERE p.author_id = #{userId}
AND p.status = #{status}
AND p.deleted_at IS NULL
